create table `#__rw_directions`
(
    id       int(3) auto_increment
        primary key,
    title    varchar(100)      not null comment 'Название направления',
    parity   tinyint           not null comment 'Чётность. 0 - в сторону нулевой станции на направлении, 1 - наоборот',
    color    varchar(7)        not null comment 'Цвет направления',
    font     varchar(7)        not null comment 'Цвет текста на направлении',
    info     text              null comment 'Инфа о контролёрах',
    is_cocon tinyint default 0 not null comment 'Кокон на направлении'
)
    comment 'Список направлений';

create index `#__rw_directions_is_cocon_index`
    on `#__rw_directions` (is_cocon);

create index `#__rw_directions_parity_index`
    on `#__rw_directions` (parity);

INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (1, 'Белорусское', 0, '#008000', '#ffffff', '<p>На рейсы в <b>Усово</b> контролёры предпочитают садиться на ст. <b>Кунцево</b> или же <b>Рабочий посёлок </b>и едут до самого Усово. За дорогу ходят 1 или (2) раза. Обратная высадка сил зла либо на этих же платформах, либо едут до Белорусского вокзала. <br /><br />Контролёры, проверяющие <b>главный ход</b>, ранее садились всегда в <b>Одинцово</b> и <b>Сетуни, </b>однако РЭКСы  проверяют уже от <b>Беговой</b>. В сторону Москвы садятся на ст. <b>Можайск, Тучково</b> и <b>Кубинка</b>. Электрички от / до Голицыно проверяются, но реже, чем дальние. В вечернее время в Можайских/Бородинских собаках контролёры не доезжают до Можайска и выходят в 90% случаев на станции Тучково, дабы начать проверять собаку, идущую на Москву. <br />На <b>Одинцовских </b>электричках также можно встретить контролёров, но вероятность этого очень мала, особенно в выходные. Проверяют их либо с Беговой либо с Сетуни. <br /><b>Контры</b> на направлении имеют достаточно низкий левел. <b>Возвращаться за "спортсменами" привычки не имеют</b>. Так что если едете большой компанией, рекомендуется просто слать контров подальше (к школоте не относится). В крайнем случае полюбят мозги 2-3 минуты и удалятся (если конечно этот маршрут у них не до вокзала).</p>
<p>В РЭКСАх обычно 4 (реже 5) и 1 (2) пассивных быдлана. На направлении работает ЦПП (обезьяны, клоуны), немножко осложняя жизнь электричкерам. <br />Единая тарифная зона по Москве заканчивается на станции Сетунь. <b>Мы же настоятельно рекомендуем всем участникам группы пользоваться тарифом "Супер-ноль".</b></p><br><h5 class="wk_header">Подвижной состав</h5>
<p>Основа подвижного состава направления - ЭР2Р и ЭР2Т депо ТЧ-14 <i>Лобня </i>составности 10-12 вагонов. Не отличается комфортом и новизной, однако последнее время всё больше и больше составов проходят КР-2. Какие-то составы лучше лучше, какие-то хуже, ибо депо Лобня все никак не может смириться с тем что ему отдали целое направление. В 2014 году запланировано поступление новых ЭД4М, некоторые уже поступили. <br /><br />Захаживают в гости электропоезда ЭД2Т ,ЭД4 и ЭД4М депо <b>Перерва</b>. Составность демихов 10-11 вагонов. Состояние разное - от помоек вроде ЭД2Т-0029 до откапиталенных ЭД4М-0035. <br />Участок Можайск - Бородино - Вязьма обслуживается 6 вагонными составами из депо Апрелевка<b>.</b> <br /><br /><b>Туалеты</b> открыты в 100% электричек, но засраны до предела. Сартир-хайдинг на свой страх и риск, желательно иметь противогаз. Бывшее депо Ильича ныне является собственностью ОАО Аэроэкспресс. Его подвижной состав представлен новыми ЭД4М, используемыми на экспрессных маршрутах.</p>
<h5 class="wk_header">Особенности движения</h5>
<p>Многие электрички до/от Усово следуют от/до станции Кунцево с платформы в сторону области с 5-го (крайнего) пути островной платформы. Кроме того, с Кунцево едут и несколько электричек до Одинцово, есть и укороченные "собаки" из Усово и Одинцово до ст. Фили. В принципе, можно пересесть на следующую электричку, но есть вариант и пройти до метро. <br /><br />На направлении много транзитных поездов на Савёловское и Курское (в разы меньше) направления (и обратно конечно). Доехав до станции Ржевская, можно так же перейти на Рижское / Ленинградское (7 мин.) направление. <br />Направление пересекается с БМО на ст. Кубинка-1, "кольцевые" составы приходят непосредственно на основной перрон станции. <br /><br />Скорости на главном ходу направления высокие, до 120 км/ч, за исключением отдельных участков. Укаждостолбность не распространена, 95% дальних электричек (дальше Голицыно) пропускают хоть что-то. <br />Особенно стоит отметить Дачное: на нём останавливаются менее 50 % проходящих электричек. <br />Поезда до Можайска и Бородино могут ехать "экспрессом" с остановками Беговая, Фили, Кунцево 1, Рабочий посёлок, Сетунь, далее - только по станциям, начиная с 109 км - везде. Ехать на таких очень удобно, т. к. они останавливаются на наилучших станциях для перебегания.</p>
<p>Платформы высокие на участке Москва - Бородино и на ответвлениях. На участке Бородино - Вязьма платформы низкие. На станциях Можайск и Бородино есть и высокая платформа и низкая, принимающая транзитные составы.</p>
<p>Направление имеет два ответвления, по которым ходят электрички из Москвы: <b>на Усово с Рабочего Поселка</b> (<b> с отдельной крайней платформы</b>) и на Звенигород с Голицыно. Усовская ветка - сугубо "курортная", идёт вдоль Рублёво-Успенского шоссе. Обратите внимание, что <b>на Усовской ветке постоянно работают разъездные кассиры. </b>Кроме того, от ст. Звенигород до города надо ехать, например, на автобусе. </p>
<p>Начиная с 2013 года, идёт "<b>модернизация</b>" участка Москва - Одинцово: строятся дополнительные пути и платформы. Ремонт затронул также Усовскую линию, на которую планируют пустить экспрессы (смысл их там весьма туманен). Будьте готовы к неудобствам: старые платформы будут разбираться, вместо них возводиться временные деревянные, возможны дневные перерывы от двух и более часов.  </p>', 1);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (2, 'Горьковское', 1, '#fefba6', '#857979', '<p>Все электрички от Курского вокзала от Москвы до Владимира обслуживаются контрами ЦППК. Условия для перебежки довольно неплохие - поезда всегда стоят дольше 15 секунд, машинисты почти всегда ждут перебегающих.</p>
<p>Контролёры ЦППК не особо дерзкие, но их теперь нередко сопровождает ЧОП, которые порой захватывают сразу по нескольку вагонов и мешают забегам + контры часто сами встают в тамбуре, и тогда придётся бежать ещё вагон-два. Контры обычно садятся на ст. Железнодорожная в область (чаще всего в последний вагон), идут один раз. Обратно - на Орехово-Зуево, Павлике или Фрязево (тоже 1 раз), после Железки уходят. В электричках до Железки контролёры могут начать идти сразу после Серпа и Молота (но редко). В электричках Москва - Петушки часто проверяют второй раз на участке Орехово - Петушки (другая бригада). В утренних крутовских, петушинских и владимирских электричках (и в Москву, и в область) на участке Железка - Орехово контролёры едут в каждом вагоне. Зайцам приходится стоять в тамбуре. На направлении несколько ответвлений - в Балашиху, Захарово и Электрогорск. Иногда контролёры работают и в Балашихинских электричках, но не жёстко.</p>
<p>Наиболее хорошие условия для перебежки на Москву на ст. Купавна в связи с тем, что платформа кривая и поезд всегда стоит долго.</p>
<p>После Петушков идёт 1 бригада контров, нередко ходят с ЧОПом, могут пройти несколько раз. Теперь почти везде в тех краях платформы на 12 вагонов, так что перебегать можно. Только нужно быть осторожными в Сушневе и 170 км (там короткая платформа). Экспресс на 6:03 из Владимира обычно проверяется только контрами ЦППК, начинающими проверку с Орехово-Зуево.</p>
<p>В экспрессах до Ногинска контролёры весьма пофигистичны, часто ходят всего один раз. В спутниках до Железки контролёров обычно нет вообще. На направлении активно работает ЦПП, создавая определенные трудности, особенно на участке Петушки-Владимир.</p>
<p>Пероный контроль последнее время полюбил Кучино.</p><br><p>Скорости на направлении выше среднего, особенно за Железкой. Если вы в будни вечером собрались ехать дальше Железнодорожного - желательно садиться на вокзале, иначе попасть в вагон будет проблематично.  <br /><br />Направление довольно буйное - часто случаются драки, на последних электричках много пьяни, срывают стоп-краны и т.д. Поздно ездить в одиночку после Железнодорожного не рекомендуется. Основные места скопления гопников - районы городов Орехово-Зуево и Покрова, также встречаются в Новогиреево, Реутово, Железке, Купавне и Электроуглях. Также иногда случается страшная вещь - толпы обезумевшего футбольного фанатья устраивают драки, разнося вагоны электричек в пух и прах (особенно от таких идиотов страдают петушинские). Если они более-менее адекватны, то начинают выгонять мирян из вагона, тех, кто хочет остаться, предупреждают, что "щас здесь буит МЯЯЯЯСО!". Если рискнёшь остаться, то это звездец - бьются бутылки, летят осколки и всё выше перечисленное. При этом имеют обыкновение закрывать на цепи с замками все двери (и тамбурные, и автоматические) до окончания своих разборок. <br /><br />С лета 2015 года вместо Сапсана по Горьковке до 5 раз в день ходит скоростной поезд Talgo (Стриж) с электровозной тягой в Нижний Новгород, перед его проследованием можно встретить появление на платформах ЧОПа и ВОХРовцев. Также по данному маршруту курсирует до 3 рейсов электропоездов-экспрессов «Ласточка». Скорости у них не особо высокие - максимум 160 км/час, поэтому риск попасть под них из-за невнимательности не намного больше, чем у обычной ускоренной электрички. <br /><br />Из Петушков в сторону Владимира отправляется только одна электричка утром, все остальные электропоезда на этом участке курсируют до Москвы. <br /><br />Направление стыкуется с ответвлением Ярославского на ст. Фрязево и пересекается с БМО на ст. Орехово-Зуево. Единая тарифная зона по Москве заканчивается на станции Новогиреево. <br /><br />В вечерних и ночных электричках до Балашихи любят прохаживаться менты на предмет проверки документов, так что берите с собой что-либо, удостоверяющее личность, особенно, если вы - не славянской внешности. </p>
<div class="wk_sub_header"><strong>Спутники</strong></div>
<p>На направлении по будням курсируют спутники "Железнодорожная - Москва-Курская" с промежуточной остановкой в Новогиреево. Время в пути 23 минуты (11 до Новогиреево). Есть ещё спутники до Ногинска с остановками на Новогирееве, Железке и Фрязеве; а также до Крутого и Орехово-Зуева с остановками, помимо прочего, в Павловском Посаде. Электрички - 4 ЦППК-шные ЭД4М 500-й серии и одна кософарая ЭД4М, все - с прислонно сдвижным дверями и гармошкой вместо обычной резины. </p>
<div class="wk_sub_header"><strong>Экспрессы</strong></div>
<p>Есть два ежедневных экспресса до Владимира, которые курсируют утром в Москву и вечером обратно, и один по будням до Орехово-Зуево. Обслуживаются ЭД4М с прислонными дверями и гармошкой. Билеты на экспресс отправлением 18:01 из Москвы вечером и 07:26 из Владимира проверяются проводниками, проехать без билета внутри почти нереально (можно цеплять в межвагонье, сзади нежелательно по причине палевности). Вход в экспресс отправлением 06:10 из Владимира и 21:01 свободный, контролёр-раннинг на вечернем маршруте реален, на утреннем - только до Орехово-Зуево, после экспресс идёт до Москвы без остановок, поэтому рекомедуется применять сортир-хайдинг. <br /><br />Существует также экспресс выходных дней до Владимира с остановками в Павловском Посаде, Орехово-Зуево и Петушках, который отправляется в субботу утром из Москвы в 7:25 утра, а из Владимира - в воскресенье вечером в 19:22. Ещё один экспресс с аналогичными остановками отправляется по четвергам и пятницам из Москвы в 15:30. <br /><br />Экспресс до Орехово-Зуево - ЭД4М с гармошкой и прислонками, билеты проверяются проводниками при входе. Возможность проезда без билета в салоне есть, но ограничена (можно спрятаться в сортир).  <br /><br />Также до 3 раз в день до Нижнего Новгорода и обратно с остановками в Орехово-Зуево и Владимире курсируют скоростные электропоезда ЭС1 Ласточка, как одиночные, так и сдвоенные. Билеты на них аналогичны ПДСам и проверяютя на входе проводниками, проехать без билетов внутри практически нереально. Возможен проезд снаружи (в межвагонье на проводах, а также на метельнике сзади или между составов, спрятавшись в нише под корпусом сбоку от сцепки), но стоит помнить, что при отправлении и прибытии за этими электричками достаточно пристально следят ЧОПы, по сигналу которых поезд могут остановить для снятия вас, чего в случае с обычными экспрессами ЭД4М и особенно собаками маловероятно. </p>
<div class="wk_sub_header"><strong>Подвижной состав</strong></div>
<p>Подвижной состав ПС, используемый на обычных маршрутах 6000-й нумерации практически полностью состоит из ЭД4М 2010-2011 годов выпуска без кондиционеров. Есть также одна ЭД4М-0268 2009 г.в. со старой конструкцией поручней и несколько новых ЭД4М с прислонно-сдвижными дверями и гармошкой. Несмотря на красивый внешний вид и теплоту зимой, летом в ЭДшках очень жарко и душно. Кроме того, на направлении пока ещё осталась одна 14-вагонная ЭР2Т, совершающая несколько рейссов в Балашиху. На некоторых обычных маршрутах также заезжают спутниковые ЭД4М с прислонно-сдвижными дверями, которые ходят на маршрутах 7000-й нумерации, включая как 500-е, так и кософарые 400-е. </p>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (3, 'Казанское', 1, '#00d1b9', '#ffffff', '<p>На Рязанском направлении контролёры предпочитают ходить в дальних электричках (чем дальше электричка, тем больше вероятность их наличия). В электричках до 47 км ходят где-то только в трети электричек, обычно садятся на Люберцах 1 в последний вагон и едут до Быково или Раменского. Также вполне можно не встретить контролёров в редких рейсах до Фаустово, Виноградово. Бежать обычно 1 вагон. Рязанские трясут почти весь маршрут, исключая Москву. <br /><br />На Казанском направлении контролёры ходят во всех поездах за редким исключением (могут забить на ранние утренние и поздние вечерние). Обычно одна бригада садится в последний вагон в Люберцах 1, или же в первый вагон в Коренёво. Выходить могут на любой остановке от Гжели до Куровской. Изредка не выходят в Куровской, а идут в обратную сторону до Шатуры. Мониторить контров очень легко, т. к. из каждого вагона бежит по 10-15 человек. На дальних маршрутах (до Черустей, в основном - транзитные до Вековки) могут быть две бригады, идущие в одном направлении на расстоянии полэлектрички друг от друга. Бежать обычно один вагон. <br /><br />И на Казанском и на Рязанском направлениях контры почти никогда не препятствуют перебегающим и не возвращаются за ними. Мало того, на Казанском направлении, которое является вторым по показателю "процент зайцев в вагоне", если контры проверили весь вагон перед остановкой, они не идут в следующий, а ждут, пока двери откроются и тамбур освободится от перебежчиков! Обычно контры ходят в сопровождении пассивного ЧОПа. <br /><br />На участке Черусти - Вековка, который потерял беспересадочное сообщение с Москвой в 2013 году (официально, на самом деле по выходным есть два прямых рейса) есть свои, суровые контры, от которых бежать сложно, но можно. Проходят один-два раза. Количество проходов обратно пропорционально количеству вагонов в поезде (от 6 до 11). <br /><br />В спутниках до Раменского контролёры иногда бывают, иногда их нет. Изредка могут быть даже почти в каждом вагоне.  <br />В экспрессах "РЭКС" до Голутвина иногда бежать приходится всего один раз, но нередко контры ходят туда-сюда, порой вообще сидят в каждом вагоне. <br />В экспрессе Москва - Рязань проехать совсем без билета очень сложно, разве что на коротких участках. Прокатывает сортир-хайдинг (но не на весь маршрут). На входе в вагоны стоят проводники, билеты ПДСного типа с указанием места. <br />В экспрессе Москва - Шатура контры примерно уровня обычных ЦППКшных, бегать можно, иногда приходится по 2 вагона. Контролёр-раннинг затруднён малым количеством остановок. </p><br><h5 class="wk_header">Подвижной состав</h5>
<p>Рязанское направление обслуживает ТЧ-7 Раменское (пл. 47 км), Казанское - ТЧ-26 Куровская. Обычные электрички на обоих направлениях - ЭД4М 2002-2009 годов выпуска. Поезда ТЧ-26 содержатся в хорошем состоянии, ТЧ-7 - в посредственном. С номера ЭД4М-0182 все электрички поставлялись с маленькими откидными "душегубскими" форточками. Для спутников Москва - Раменское используются кософарые ЭД4М, для экспрессов Москва - Голутвин - ЭД4М-0352 и 0353. Экспресс Москва-Шатура - ЭД4М-0483.</p>
<h5 class="wk_header">Особенности направлений</h5>
<p>Направления идут вместе от вокзала до ст. Люберцы 1. Рязанское направление идёт южнее, являясь изначальным главным ходом Транссиба. Казанское - севернее, в сторону Мурома. Считается, что участок Москва - Люберцы 1 принадлежит Рязанскому направлению. Но некоторые называют оба направления в целом Казанским направлением. <br /><br />На участках Москва - Рязань и Москва - Коренёво движение поездов по всем главным путям левостороннее. <br /><br />На участке Москва - Раменское 4 главных пути. Обычно пригород ходит по 1/2 с большим количеством остановок, но некоторые дальние электрички и экспрессы - по 3/4. По 3/4 путям возможны остановки: Москва, Сортировочная (в Москву), Новая (в Москву), Фрезер (в Москву), Перово (в Москву), Выхино, Косино, Люберцы 1, Фабричная (из Москвы), Раменское (электропоездам следующим далее нет остановки на пл. 47 км). <br /><br />Рязанское направление было отремонтировано в середине двухтысячных на участке Москва - Раменское. Были обновлены все платформы, подвижной состав, многие станции и платформы получили турникеты. На Казанском направлении обновился только подвижной состав. Многие платформы в плохом состоянии, некоторые разваливаются (73 км). <br /><br />На Рязанском направлении скорости низкие на участке Москва - Раменское из-за большого количества близкорасположенных остановок, в остальном - относительно высокие. Пропускают некоторые остановки в основном электрички, следующие далее 47 км. Но не всегда: есть даже голутвинские со всеми остановками. На Казанском направлении в середине двухтысячных были отменены некоторые поезда за счёт добавления остановок другим, так что подавляющее большинство поездов ходит со всеми остановками. Но за Гжелью, где расстояние между остановками становится больше, сильно увеличиваются и скорости. <br /><br />Имеются ответвления: Голутвин - Озёры (ходят РА2 зимой и ДП1-М летом, 30 пар в неделю), Кривандино - Рязановка , Рыбное - Узуново, Люберцы-2 - Дзержинский и Луховицы - Зарайск (два последних сейчас чисто грузовые). <br /><br />Высокие платформы на участках Москва - Голутвин и Москва - Вековка, кроме о.п. Мильцево, ст. Струя в сторону Вековки. На участке Голутвин - Рязань и ответвлениях платформы низкие, перед/после Голутвина помощник проходит по составу, поднимает/опускает фартуки- обеспечивает лёгкий выход на низкую платформу, так что перебегать даже по насыпи не проблема. <br /><br />Казанское направление пересекается с БМО на ст. Куровская. Рязанское - на пл. 88 км, ст. Воскресенск. Единая тарифная зона по Москве заканчивается на станции Косино.</p>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (4, 'Киевское', 0, '#00ddff', '#ffffff', '<p>Контролёры на направлении вполне адекватные. Иногда возможен договорайтинг. Проверяют обычно 1 раз до Нары после Солнечной или Внуково, после Нары иногда ходят Калужские, которые более суровые, но перебежать можно. Ближе к Калуге и при движении от нее могут повторно пройтись или вернуться к подозрительным пассажирам, кого не видели, и пригрозить полицией, но если не оставаться в их вагоне или соседнем, это не делают. Важно заметить, что машинисты ждут электричкеров почти всегда.</p>
<p>В электричках до Солнечной, Лесного Городка и Аэропорта контролёров обычно не бывает, иногда не проверяются Апрелевские собаки. В случае усиленной работы контролёров на Калужской электричке до Москвы, пересаживание на Наровскую на Зосимовой Пустыни, или в самой Наре, работает - она и свободнее, и бригада обычно одна и проверяет мало. Электрички на БМО обычно проверяются перед Апрелевкой. По пути в/из Калуги стабильно проходят три раза.</p><br><div class="wk_sub_header"><strong>Подвижной состав</strong></div>
<p>Подвижной состав - почти все ЭД4М, некоторые - последних поставок и в отличном состоянии. ЭРок почти не осталось. На аэроэкспрессных маршрутах стоят красные ЭД4М. Писк моды - ЭГ2Тв, только на Новопеределкино, Электропоезд Городской Тверской 5-вагонная поделка Тверского завода, на линии с апреля 2017. Калужские экспрессы - ЦППКшные ЭД4м 04хх.</p>
<div class="wk_sub_header"><strong>Особенности направления</strong></div>
<div class="wk_sub_header"> </div>
<div class="wk_sub_header"><span style="color: #ff0000;">На Киевском направлении присутствуют терминалы предв. оформления проездных документов. Это значит, что брать билеты у контролёров нельзя вообще, так как они будут ссылаться на то, что у вас отсутствует талон, который нужно было взять в таком терминале, и в связи с этим требовать сбор 100 рублей.</span></div>
<div class="wk_sub_header"> </div>
<div class="wk_sub_header">На направлении присутствуют аэроэкспрессы до ст. Аэропорт Внуково. Обычные электрички Москва - Аэропорт идут не в само Внуково, а до старой платформы "Аэропорт", где находится Авиаремонтный завод. Идти до аэропорта оттуда очень долго, но есть пересечение с автобусами до Москвы и аэропорта. <br /><br />Направление проходит через московские районы Матвеевское, Очаково, Солнцево, Новопеределкино, располагающиеся за МКАДом. Пассажиры из этих районов составляет основу пассажиропотока до Нары на направлении. <br /><br />Существуют транзитные рейсы на БМО: Москва - Посёлок Киевский - Мачихино - Кресты, Калуга - Кресты. В таких поездах контролёров раньше не было, теперь появились на главном ходу, обычно возле Переделкино. Кроме того, иногда контролёры бывают на БМО. <br /><br />Если Вы едете в город Калуга, то Вам потребуется электричка до станции Калуга-1. Если же Вам надо ехать транзитом в Сухиничи или Брянск, ехать нужно до станции Муратовка, на которой пересаживаться на электричку в сторону Калуги-2 и дальше на Сухиничи.  <br /><br />Существуют электрички до Апрелевки из Калуги и БМО. Это шести-восьмивагонные ЭРки Калужского или кольцевого графика. Как правило, через 10 минут после их прибытия в Апрелевку, уходит пустая электричка в Москву. (с июня 2017 года данный электропоезд продлён до Москвы)<br /><br />Направление пересекается с БМО на ст. Бекасово-1, посадка в собаки маршрутов чисто по кольцу, а также Кресты-Калуга и Калуга-Кресты - с отдельной низкой платформы, в собаки, следующие из Апрелевки в сторону Кубинки - с высокой. (Но бывают исключения, внимательно слушайте обьявления на станции!)<br /><br />Единая тарифная зона по Москве заканчивается на платформе Переделкино. <br /><br />Часть электричек от Солнечной до Москвы следуют по 3-ему пути, то есть после Сколково останавливаются на редко используемой платформе. </div>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (5, 'Кольцевое (БМО)', 0, '#000000', '#ffffff', null, 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (6, 'Курское', 0, '#0026ff', '#ffffff', '<p> Направление обслуживается контрами Центральной Пригородной Пассажирской Компании. Условия для перебежки очень хорошие - поезда всегда стоят дольше 20 секунд, даже на малолюдных станциях. Все платформы высокие, вмещают 11ти вагонную ЭД-шку (в том числе узкие платформы в Тульской области), исключения - Ока(4 - 5 вагонов); на Ревякино и Хомяково не влазит последний вагон в сторону Тулы; на Шульгино и пл. 153 км - один-два последних в сторону Москвы. Есть мизерный шанс остаться на платформах с числовым названием (например, 66 км), на Колхозной, Чепелёво, Авангарде, хотя народ активно успешно перебегает и там.</p>
<p>Чаще всего по направлению от Москвы контролеры начинают ходить от станции Бутово, так как это последняя станция, находящаяся на территории Москвы, тем самым пресекая тех, кто берет билет по Москве. <br />Иногда садятся в Царицыно и начинают проверять с Царицыно. При поездке по направлению в Москву контролеры часто выходят в Царицыно. В дальних электричках проверяют, как правило, с Подольска. Некоторые Щербинские и Подольские электрички могут проверять между вокзалом и Царицыно! На дальних электричках контролеры после Чехова проходят мимо, билеты не проверяют. <br /><br />Контролёры довольно пофигистичные, перебежка обычно всего один вагон. Однако есть маленький шанс, что вредная контролёрша устроит засаду в тамбуре и придётся бежать ещё.<br /><br />За Серпуховым с недавнего времени условия для езды без билета ухудшились, контролёры до Тулы проходят 3-4 раза, есть вариант, что перебегающих в очередной раз могут прижать. Садятся в Серпухове в начало и конец элки и ходят туда-сюда до самой Тулы. Впрочем, после первой перебежки можно попробовать наёбинг, это уже успешно осуществлялось.</p><br><p>Скорости на участке между Щербинкой и Серпуховым высокие - ощутимо быстрее Ярославки, но медленнее Ленинградки. После Серпухова, в Тульской области, скорости поездов падают, особенно от Оки до Приокской. <br /><br />В черте Москвы на станции Перерва находится депо "Перерва" (ТЧ-3), поезда которого обслуживают Курское, Белорусское и Рижское направления. В связи с этим некоторые поезда от вокзала следуют только по Москве, до ст. Люблино, а часть поездов из области - до Царицыно. Существует немало транзитных поездов на Белорусское и Рижское направления, часть составов идёт до Каланчёвской, некоторые поезда с других направлений вплоть до Серпухова. Также очень часто можно встретить ЭП2Д, заезжающие транзитом с депо Нахабино.</p>
<p>Между далёкими уголками Курского хода - Тулой ходят несколько пар электричек шестивагонной составности, приписанных к уч. депо Новомосковск-1 (ТЧ-22).</p>
<p>На направлении есть великолепный ночной лайнер Голицыно-Серпухов, который не првоеряется на всём участке. Заполняется бомжами ещё по пути в Голицыно, которые там оборачиваются и едут в Серпухов. Ехать лучше всего в первых вагонах.</p>
<p>В черте Москвы Курское направление проходит более 30 км и делает большой крюк - рекорд по километражу среди других направлений Москвы. В связи с этим многие садятся на Царицыно или Текстильщиках, а не на вокзале, также много внутримосковских пассажиров.</p>
<p>На Курском направлении существует экспресс до Серпухова 7XXXой нумерации, электропоезд типа ЭД4МК, с повышенными ценами на билеты. Во многих вагонах сидят контролёры, но порой проехать бесплатно нетрудно. Особенно если Вам надо от Подольска до Царицыно или наоборот.<br /><br />Платформа Битца находится на территории Московской области возле МКАДа, хотя и предшествующая ей ст. Красный Строитель, и находящаяся за ней пл. Бутово - территория Москвы.<br /><br />Любителям железнодорожного транспорта на заметку - рядом со станцией Щербинка есть экспериментальное железнодорожное кольцо ВНИИЖТа (справа перед станцией если ехать из Москвы). Там находится опытный полигон, куда часто пригоняют новые поезда, также на территории стоит много интересных малосерийных поездов. В день железнодорожника (1 воскресенье августа) там часто устраивают покатушки на старой электричке Ср3 или вагонах под старым электровозом ВЛ22. Единственный минус для желающих побродить там - по территории бегает много диких собак, ещё могут встретиться недружелюбные ЧОПы, не пускающие на территорию депо (возможно обойти их со стороны путей). Чаще всего они сидят по будкам, редко выползая наружу, и в "мирное" время особых проблем от них нет (если не заходить внутрь зданий или огороженную территорию за забором), но во время выставок или дня железнодорожника их активность возрастает, и любые попытки сделать шаг влево - шаг вправо вызывают агрессию. <br /><br />Красивые пейзажи на перегоне на участке от Серпухова до Тарусской (пойма Оки, мост через Оку), есть неплохие виды на участке Битца - Бутово, Силикатная- Подольск. <br /><br />Единая тарифная зона по Москве заканчивается на ст. Бутово.<br /><br />Направление пересекается с БМО на ст. Столбовая, посадка в кольцевые электрички с низкой платформы.</p>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (7, 'Ленинградское', 1, '#e3bd76', '#ffffff', '<p>Контролёры как правило ходят с ЧОПовцами в количестве 1-2 человек. Форма у контролёров чёрная РЖД-шная или своя + фирменная синяя или зелёная накидка сверху и красная у ревизоров, а летом деловой костюм. Говоря о суровости, и о кадров, они довольно лояльны, однако могут прижать в тамбуре и выбить дань. Иногда бежать приходится 2 вагона (при облавах могут проверять 2-3 бригадами с разницей в 1-2 вагона). Идут контры довольно быстро (возможен лажатикетвпаринг).</p>
<p>Из-за средних скоростей стоят на станциях собаки недолго, на некрупных остановочных пунктах секунд по 15 (в графике узаконена стоянка по 30 секунд). Так что перебегать лучше на крупных станциях. На участке Москва - Крюково это неактуально после ввода зимнего графика 2015 года - теперь даже на НАТИ останавливается довольно много элок, и шанс долгого туповейтинга не высок - можно спокойно перебегать где угодно. Многие машинисты ждут, а некоторые получают оргазм от того, что оставили пару зайцев в каком-нибудь Московском Море. Крайне не рекомендуется перебегать по деревянным временным платформам, особенно в тёмное время суток.</p>
<p>Схема проверки билетов одинакова, но примерно раз в месяц они ходят иначе, но на следующий день она такая же. Могут так же от начать идти с двух сторон или же идти в 2 бригады и чередоваться вагон-вагон. Иногда устраивают рейды в 2-3 бригады сразу, растягиваясь на несколько вагонов. Тверские электрички наиболее проверяемые, как правило 1 до Клина и еще 1 - после. Первые и последние электрички в направлении "от Москвы" зачастую не проверяются. Последняя электричка от Твери до Москвы проверяется от случая к случаю, возможно появление контров в районе Клина. На первых электричках очень часто присутствуют ревизоры, особенно в первых Конаковских и Тверских.</p>
<p> <a href="http://elektrichking.ru/manual/terms/control/cocons">Подробнее о ревизорах</a></p>
<p>На неотурникеченных крупных станциях на участке Москва - Крюково (Ховрино, Химки, Сходня, Крюково) МТППК активно применяет перронный контроль. Методика преодоления рассмотрена на страницах станций. В конце 2015 года, к примеру, в Крюково, живыми турникетами работало 26 человек. Из чего можно сделать вывод, что компания-перевозчик расходует средства на зарплату бездельникам, которые препятствуют проходу разве что совсем начинающих и пугливых электричкеров, и в основном доставляют неудобства пассажирам с билетами. </p><br><p>С 1 октября 2015 года вместо экспрессов введены Ласточки до Крюково и Твери. В связи с этим полностью ликвидируется явление быстрых "зелёных" электричек. Теперь обычные электрички едут почти со всеми столбами, в том числе тверские и конаковские. <br />Скорости на Ленке с зимнего графика 2015 г. понизились из-за ввода тактового движения Сапсанов (которые, к тому же, ходят парами) каждые 2 часа и Ласточек до Крюково и Твери. Из-за большого количества скорых и высокоскоростных поездов, которые надо пропускать, к примеру, в Крюково, Поварово-1 и Подсолнечной, были слухи о стоянках в Клину и Редкино. Если же вас собирается обгонять Сапсан, то готовьтесь стоять где-нибудь между станциями и пропускать его. Хотя в последнее время график выстраивают так, чтобы минимизировать стоянки для пропуска Сапсанов. Впрочем, если вдруг на направлении кто-нибудь сдохнет, то у Сапсанов и ПДСов максимальный приоритет, а электрички зарабатывают опоздания вплоть до 2-3 часов.</p>
<p>На старой платформе Рижская, имеющей пересадку на Ржевскую элки не останавливаются (кроме крайне редких исключений во время ремонта), пригород полностью разделён с ПДСными путями аж до Останкино. Интересно, что в 2015 году во время подключения 4 пути и переукладки стрелок в пригородной горловине вокзала, были введены электрички от Крюково до Курского вокзала через Алексеевскую соединительную линию.</p>
<p>Тверь, центр соседней со столицей областью, имеет отличное пригородное сообщение, чем выделяется среди других подобных городов (Тула, Владимир, Рязань). Даже без традиционных пробок на Ленинградке, время езды на электропоезде и автобусе до Москвы одинаково. <br /><br />После Решетниково есть ветка от главного хода на Конаково. Там иногда встречаются контры, и низкие скорости у электронов, зато долгие стоянки на остановках - можно перебежать пару вагонов. <br /><br />Единственное направление на Мосузле, где у турникетных тёток раньше было ОЖДшное ПО на компах, а в окне рядом с кассиром был повёрнутый в сторону пассажира широкий экран с отображением 2 ближайших рейсов в обе стороны. С 2013 года всё это демонтировано, что намекает на "заботу" о пассажирах.</p>
<p>С точки зрения криминогенной обстановки, Ленка довольно безопасна. Хотя в поздних тверских электричках (особенно по направлению в Москву) могут возникать неприятные ситуации. </p>
<p>Интересная особенность - левостороннее движение электропоездов на участке Останкино — Москва-Пассажирская. Поэтому, садясь на электричку на платформе Рижская или Останкино, нужно быть внимательным, чтобы не уехать не в ту сторону.</p>
<p>Все электрички — ЭТ2М, 2002-2010 гг. выпуска. Экспрессы — "Ласточки" (ЭС1 и ЭС2Г). Обычные электропоезда 12-вагонные, экспрессы — 10-вагонные (2 сцепки по 5 вагонов).</p>
<p>Ленка — единственная направа на Московском узле с электропоездами типа ЭТ2М, отличаются от ЭДшек узкими тамбурами (как у ЭР2) и неудобными антивандальными пластиковыми сидениями, кроме новых серий. Например, в ЭТ2М с № 136 – 143. На данный момент не выпускаются. Так же была проведена модернизация некоторых электропоездов. Была заменена обшивка салона, замена кресел и окон, установка кондиционеров, камер видеонаблюдения, системы освещения. В целом, салон стал более светлым и аккуратным. В зимний период хорошо держит тепло. </p>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (8, 'Павелецкое', 0, '#b3e6b6', '#050505', '<p>Самое спокойное направление на мосузле. Чаще всего электричку проверяет один раз - одна бригада из двух контролёров (иногда с охраной), в этом случае избежать встречи с ними не составляет особого труда.</p>
<p>Ближние электропоезда (не далее Михнево) порой не проверяются вообще, либо проверки довольно пофигистичны, нередко удаётся "отсидеться" в тамбуре.</p><br><p>На направлении есть обыкновенные "зеленые" электрички прямо в аэропорт (не куда-то рядом, а именно прямо на ту же платформу в аэропорту, куда также прибывают Аэроэкспрессы). <br /><br />Пассажиропоток по сравнению с другими направлениями ощутимо ниже. Если, к примеру, на соседнем Курском направлении утром в понедельник из Подольска вы предпочтете уехать в Москву исключительно на электричке отправлением именно из Подольска (если вы не рьяный фанат биткомрайдинга), то тут вполне можно даже после Бирюлева занять сидячее место.  <br /><br />Направление проходит через московский район Бирюлево, имеющий ощутимые проблемы в транспортной инфраструктуре, так, например, метро в районе отсутствует. Поэтому многие жители этого района активно используют именно жд транспорт, и не следует удивляться, когда вечером при следовании из Москвы в область вы увидите активное движение в сторону выхода на станциях в районе Бирюлево. Существуют даже внутримосковские рейсы: Москва - Бирюлево-Пасс.<br /><br />До Ступино на направлении присутствует третий главный путь. Используется грузовыми поездами и Аэроэкспрессами. Третий путь на участке от Домодедово используется мало (в основном товарняками), электрички там ездят только в случае ремонта одного из двух других путей. Для этого на остановочных пунктах: Взлётная, Востряково, 52 км (?), Вельяминово, Привалово, Шугарово - предусмотрена посадка и высадка из первых 2-х вагонов.  <br /><br />На направлении имеются относительно недавно появившиеся платформы 32 км и 85 км. Из-за того, что места между путями недостаточно для постройки островной платформы, и там и там имеются только боковые платформы (на 32 км - на 2 вагона), на которых останавливаются электрички из Москвы. Некоторое время назад (в конце 90-х) на пл. 32 км. была построена короткая высокая боковая платформа на 3-м главном пути, но весной 2009 г. демонтирована за ненадобностью. Существуют планы строительства полноценной (на 12 вагонов) островной платформы между 1-м и 2-м путями (с передвижкой 1-го главного пути) для обслуживания одного из крупнейших в Подмосковье логистических парков - "Северное Домодедово", но неизвестно, когда они будут реализованы. <br /><br />На всех станциях платформы высокие, но на участке Ожерелье - Узуново они довольно короткие, посему бегать на указанном участке бывает трудно и контры могут зажать в хвостовом вагоне (хотя, их там обычно не бывает). <br /><br />На ст. Ожерелье можно пересесть на дизель-поезда РА2 в сторону Узловой и Новомосковска.<br /><br />Существуют экспрессы частного перевозчика "Регион-Экспресс", работающие под маркой РЭКС, в Домодедово, Барыбино и Ожерелье. Используются только брендированные составы в синей фирменной окраске с белым логотипом - собачкой. Во всех ходят контры на протяжении всего пути. <br /><br />Летом есть электрички, идущие по выходным дням с вокзала на БМО (до ст. Яганово), на зиму отменяются. Также обычные электрички весь год ходят по ответвлению в аэропорт Домодедово.  <strong><br /></strong></p>
<p><strong>Подвижной состав.</strong></p>
<p>Почти все поезда - новые ЭД4М с прислонными дверьми. Однако встречаются раритеты - есть два состава с деревянными лавками. Направление также обслуживает БМО, куда пускают ЭМ2.</p>
<p> </p>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (9, 'Рижское', 0, '#00bd5b', '#ffffff', '<p>Обычные бригады могут попасться где угодно на каких угодно рейсах в количестве 1-3 на состав (одна бригада). В массе не агрессивны, если с ними не контактировать или игнорировать в тамбуре до перебежки. Перебегать стоит на крупных станциях вроде Нахабино, Дедовск и т.д., поскольку на полустанках времени может и не хватить. Причём по Нахабино часто происходит смена бригад, потому стоянка 3 минуты и можно даже перейти, а не перебежать.</p>
<p>На Нахабинских рейсах обычно садятся на Дмитровской, Ленинградской, Тушино, иногда даже Ржевской. На дальних рейсах встречаются после Дедовска. Экспресс проверяется от Волоколамска до Курского вокзала, в обратную сторону от Курского вокзала до Волоколамска.</p>
<p>Касса на Аникеевке работает только по будням. Расписание касс можно посмотреть справа.</p><br><p>На направлении около 20 старых ЭРок, состояние салона в среднем на четверочку - тепло и лампово, около 15 поездов типа ЭД4 (часть из которых - 200-е и 300-е, выпущенные до 2012; а часть - 400-е, в том числе и белые от ЦППК), Шаховской и Волоколаский экспрессы (ЭД4М-0434 и ЭД4М-0236). С Курского направления заезжают, в-основном, кастрированные ЭД-шки депо Перерва разных мастей, салон которых чаще всего засран. В 2016 году в депо Нахабино поступили поезда нового типа - ЭП2Д, которые являются логическим продолжением поездов ЭД4М 500-й нумерации.</p>
<p>От Павшино и Красногорской проезд до Ржевской и Каланчевской дешевле на одну зону. По билету до Рижского вокзала можно выйти на Ржевской.</p>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (10, 'Савёловское', 1, '#ffa600', '#ffffff', '<p>Контролёры не особо придирчивые, уйти от них нетрудно. Иногда сопровождаются пофигистичным ЧОПом. При этом, в дальних эльках часто тусуют по две бригады за раз, и сниматься с насиженного места придется достаточно рано. Касаемо безопасности, гопарей на Савке практически не бывает (особенно в районе наукограда Дубна - там преступности вообще очень мало). Исключение - окрестности города Кимры Тверской области, т.е. ст. Савёлово. Кимры - полная противоположность Дубне, город гопов и наркоманов, поэтому на электричках до Савёлово и обратно вечером лучше не ездить.</p>
<p>Очень много рейсов сопровождаются сотрудниками полиции, особенно в черте Москвы и в ближайшем Подмосковье.</p><br><div class="wk_header"><strong>Подвижной состав</strong></div>
<p>Все электрички на обычных маршрутах - большинство ЭР2Р и ЭР2Т, все в не очень хорошем состоянии, сборные солянки. Однако в 2015 и 2016 годах подвижной состав обновился, имеется несколько "кософарых" ЭД4М с прислонно-сдвижными дверями.</p>
<div class="wk_header"><strong>Особенности направления</strong></div>
<ul class="listing">
<li><span class="l">Некоторые электрички идут транзитом с/на Белорусский ход.</span></li>
<li><span class="l">Любимая тактика контролеров данного направления в случае средней загруженности, на электричках в середине дня - не делить поезд на зоны ответственности разных бригад, как на Ярославском направлении, к примеру, а когда все контролеры идут по составу, растянувшись на два, а при наличии большого количества контров -  на три вагона, поэтому следует быть готовым перебежать три вагона за раз, и действовать сообща, сообщая другим электричкерам об этом. Особенно это верно на собаках от/до Савелово. От Москвы чаще начинают проверять после Лобни, а в сторону Москвы проверяют до самой Окружной таким вот образом. В большинстве случаев попавшимся в очаг предлагается просто выйти.</span></li>
<li><span class="l">Скорости на направлении средние, за Дмитровом - большие. На станциях поезда стоят секунд по 10-20. Условия для перебежек неплохие - порой стоят немного, но бегунов ждут.</span></li>
<li><span class="l">Участок от Москвы до Дмитрова двухпутный, далее идет однопутка. На ст. Вербилки начинается однопутное ответвление от главного хода на Дубну. За ст. Савёлово электрификация кончается и начинается Октябрьская ЖД.</span></li>
<li><span class="l">С Савёловского вокзала не отправляются поезда дальнего следования, соответственно, там нет варианта "обход через платформу ПДС".</span></li>
<li><span class="l">Вопреки распространенному заблуждению по Савеловскому направлению ходит и пассажирский поезд. Это поезд 602Я Москва - Рыбинск, который идет транзитом с Белорусского направления и, кстати, имеет остановку на станции Савёлово (32 мин.). Так же по Савку ездят транзитные ПДСы с Ярославки через БМО и Белорусский вокзал на Курское/Павелецкое направление. <br /></span></li>
<li><span class="l">На направлении достаточно агрессивные менты, могут прессануть курильщиков в тамбурах.</span></li>
</ul>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (11, 'Ярославское', 1, '#008dd2', '#ffffff', '<p>Направление обслуживается контролёрами Центральной Пригородной Пассажирской компании (ЦППК). Контров немного, ходят обычно парами, иногда с одним быдлочоповцем, которому часто пофигу, или с двумя быдлочоповцами, которыми уже далеко не пофигу. Очень редко могут согнать на электричку аж целый отряд быдлочопа, но, впрочем, это не проблема. Растягиваются (как правило) на 1,5-2 вагона.</p>
<p>На главном ходу контролёры появляются обычно от Пушкина (намного реже от Мытищ) и прочёсывают электричку до Сергиева Посада (затем на участке от Сергиева Посада до Александрова могут пойти обратно, раньше такое встречалось редко, но последнее время всё чаще). По пути от Москвы чаще идут с головы поезда, в обратном направлении - с хвоста. Также практикуют метод прочёсывания электрички двумя группами юнитов, двигающихся каждая через один вагон. Очень удобно ехать на электричке, которая до Пушкино делает остановки на главных, а дальше у каждого столба.</p>
<p>Контролёр-раннинг до 3-х вагонов на Ярославке реально выполнить на каждой платформе. Кстати, иногда у контров в электричках до Александрова, Фрязево (на Москву) месячные - они заходят в нужный электрон группами и ходят по нескольку раз.</p>
<p>На фрязевской ветке зло спавнится в Мытищах, и прочёсывает электричку до Гагаринской, где в 90% покидает свои позиции и, путём перехода путей в неположенном месте, ожидает обратную электричку. Перегон Фрязево - Монино проверяется по звёздам, если звёзды сойдутся - можно даже поспать (Внимание! Старайтесь не использовать платформ-кроссинг в этом месте - есть вероятность застрять на очень продолжительное время). По наблюдениям контра на Фрязевской ветке от Болшева на фрязевских электричках появляется чуть чаще, монинские проверяют реже, болшевские беспалевны; иногда бывают на Фрязинском ответвлении.</p>
<p>В Спутниках контры иногда бывают, но они весьма опытные и могут конкретно достать электричкера, от них лучше по возможности уходить дальше. Контролёр-дуринг - не заряженная карта на ветке Монино прокатывает в 95%. В спутниках до Болшево ситуация аналогичная.</p><br><p>Основной подвижной состав направления - ЭД4М. Среди них очень много новых ЭД4М депо "Москва-2" с кондиционерами и новыми межвагонными переходами без возможности зефиринга. Также есть ЭД2Т и небольшое количество ЭР2Р и ЭР2Т, которые ходят в-основном до Александрова и Сергиева Посада. Спутниками в Мытищи, Пушкино и Болшево раньше ездили исключительно ЭМ4, сейчас в-основном ЭД4М со спутниковым салоном.</p>
<p>Направление не слишком опасное. Немного некомфортно может быть вечером в электричках до Александрова, а также на Фрязинском, Красноармейском ответвлениях и на участке Монино-Фрязево. По статистике, наибольшее количество актов вандализма на направлении происходит вечером в районе Фрязино.</p>
<p>Именно по Ярославскому ходу прошли первые в РСФСР электрички. Это произошло в 1929. Даже в 1980-е годы поезда на участке до Мытищ ходили куда быстрее. Главная причина нынешних низких скоростей- загруженность участка. <br /><br />Именно на Ярославке первыми появились Спутники, в 2004 году. К этому событию модернизировали все станции и платформы до Мытищ, положили плитку, сделали навесы и заборы нового типа. Первоначально эксплуатировались составы типа ЭМ4, модернизированные ЭР2, однако вскоре оказалось, что Эмочки эти крайне ненадёжны и крайне дорогие. По "скоростным" путям ездят также обычные электрички, преимущественно скоростные, с остановкой на Лосиноостровской. Выигрыш во времени от скоростной собаки - около 11 минут. Соответственно, часто можно на Мытищах сесть в первую подошедшую псину, но вскоре Вас обгонит другая электричка.</p>
<div class="wk_sub_header"><strong>Особенности направления</strong></div>
<ul class="listing">
<li><span class="l">Помимо главного хода, на Ярославке есть ответвления - после Мытищ на Фрязево - примыкание к Горьковскому направлению - и на Фрязино от Подлипок Дачных. По первому ответвлению большинство поездов идёт до Монино, а Спутники до Болшево и Монино. От Софрино есть ответвление на Красноармейск. Кроме того, ранее существовала ветка на Пирогово, популярная у туристов и дачников. Под выдуманным предлогом "малого пассажиропотока" пассажирское движение закрыто в 1997 году, полностью линия закрыта и разобрана в 2001. Подробнее можно прочесть в Википедии.</span></li>
<li><span class="l">Основная черта Ярославки - фирменная серо-синяя окраска. В серо-синюю гамму красок покрашены составы, таблички на станциях, ограды.</span></li>
<li><span class="l">Ярославка - самое загруженное направление МЖД. В часы пик интервалы движения поездов на таких станциях, как Мытищи, напоминают метрошные, все составы - строго 11 вагонов ЭД. Но всё равно, увы, часто в вагонах творится битком-райдинг.</span></li>
<li><span class="l">Скорости на направлении низкие, особенно на участках Москва-Мытищи (из-за сильной загруженности участка), на ответвлении на Фрязино (в связи с большим количеством кривых на участке). На перегоне от столицы до Мытищ более-менее быстро едут только составы, идущие по путям для "Спутников" с остановкой на Лосиноостровской или вообще без остановки.</span></li>
<li><span class="l">Из-за такого пассажиропотока между Москвой и Мытищами аж 4 пассажирских пути (что ничего особенного - на Участке Москва-Раменское эти 4 пути аж с 192* года). Но их не хватает - поезда на этом участке тащатся медленно.</span></li>
<li><span class="l">Направление связи с метро практически не имеет - кроме вокзала, невдалеке от метро расположены платформы Москва-3 и Маленковская.</span></li>
<li>На направлении очень много торгашей и псевдоинвалидов. Благо электрички ходят часто, ждать другой состав недолго. И пассажиров хватает.</li>
<li><span class="l">Скоростные (3 и 4) пути от вокзала до Лося с левосторонним движением.</span></li>
<li><span class="l">Направление пересекается с БМО на посту 81 км, ст. Александров.</span></li>
<li><span class="l">Единая тарифная зона по Москве заканчивается на платформе Лось.</span></li>
</ul>', 0);
INSERT INTO `#__rw_directions` (id, title, parity, color, font, info, is_cocon) VALUES (12, 'Алексеевская соединительная линия', 0, '#945d0a', '#ffffff', null, 0);